import {RFC_1928_REPLIES} from '../constants'

import {SocketError} from '../../types/utils'

export function convertSocketErrorToRpcReply(error: SocketError) {
  if (error.code === 'EADDRNOTAVAIL') return RFC_1928_REPLIES.HOST_UNREACHABLE
  if (error.code === 'ECONNREFUSED') return RFC_1928_REPLIES.CONNECTION_REFUSED

  return RFC_1928_REPLIES.NETWORK_UNREACHABLE
}
