module.exports = {generateEgTunnelError, getErrorStatusCode, getOriginalError}

function generateEgTunnelError(error, statusCode) {
  return {error, statusCode}
}

function getErrorStatusCode(egError) {
  return egError.statusCode || 500
}

function getOriginalError(egError) {
  return egError && egError.error ? egError.error : egError
}
