class FrpcDownloadError extends Error {
    constructor(downloadUrl, downloadDirectory, frpcPath, ...params) {
      super(...params);
  
      // Maintains proper stack trace for where our error was thrown (only available on V8)
      if (Error.captureStackTrace) {
        Error.captureStackTrace(this, FrpcDownloadError)
      }
  
      this.name = "FrpcDownloadError"
      // Custom debugging information
      this.userError = true
      this.downloadUrl = downloadUrl
      this.downloadDirectory = downloadDirectory
      this.frpcPath = frpcPath
    }
}

  module.exports = FrpcDownloadError
